import React from 'react'
import {Container, Row, Button} from 'reactstrap'
import {NavLink, Link} from 'react-router-dom'

import logo from '../../assets/images/logo.png';
import Home from '../../pages/Home';
import './header.css';

const nav__links=[
  {
    path:'/home',
    display:'Home'
  },
  {
    path:'/career',
    display:'Career Opportunities'
  },
  {
    path:'/about',
    display:'About Us'
  },
  {
    path:'/contact',
    display:'Contact Us'
  },
]

const Header = () => {
  return (
    <header className="header">
      <Container>
        <Row>
          <div className="nav__wrapper d-flex align-items-center justify-content-between">

            {/* Logo */}
            <div className="logo">
              <img src={logo} alt="logo" to={Home}/>
            </div>
            {/* Logo */}

            {/*menu-start*/}
            <div className="navigation">
              <div className="menu d-flex align-items-center gap-4">
                {
                  nav__links.map((item,index) => (
                    <ul className='nav__item' key={index}>
                      <NavLink to={item.path} className={navClass=> navClass.isActive ? "active__link" : ""}>{item.display}</NavLink>
                    </ul>
                  ))
                }
              </div>
            </div>
            {/*menu-end*/}

            <div className="nav__right d-flex align-items-center gap-4">
              <div className="nav__btns d-flex align-items-center gap-4">
                {/* <Button className='btn secondary__btn'><Link to='/login'>Login</Link></Button> */}
                <Button className='btn primary__btn'><Link to='/login'>Job Seeker</Link></Button>
              </div>
              <span className='mobile__menu'>
              <i class="ri-menu-line"></i>
              </span>
            </div>
          </div>
        </Row>
      </Container>
    </header>
  )
}

export default Header
